mapper.select:
select id,title,picture,channel_id,category_id,user_id,
hits,hot,status,created,updated,commentCnt,articleType
from cms_article  where channel_id=#{chnId}

and category_id=#{catId}

and status=1 and deleted=0

select id,title,picture,channel_id,category_id,user_id, hits,hot,status,created,updated from cms_article where deleted=0
and status =#{status}
order by created desc
